import React, {useState} from 'react'


export default function TextForm(props) {
    const handleUpClick = () => {
        // console.log("The button is clicked" + text);
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleOnChange = (event) => {
        setText(event.target.value);
    }
    const [text, setText] = useState("Enter text here...");
    //setText("Enter the fucking text here.....");
  return (
    <div className="container">
        <h1>{props.heading}</h1>
        <div className="input-group mb-3">
            <div className="input-group-prepend">
                <span className="input-group-text" id="basic-addon1">@</span>
            </div>
            <input type="text" className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1" value={text} onChange={handleOnChange}/>
        </div>

        <div className="input-group mb-3">
            <input type="text" className="form-control" placeholder="Recipient's username" aria-label="Recipient's username" aria-describedby="basic-addon2"/>
            <div className="input-group-append">
                <span className="input-group-text" id="basic-addon2">@example.com</span>
            </div>
        </div>

        <label htmlFor="basic-url">Your vanity URL</label>
        <div className="input-group mb-3">
        <div className="input-group-prepend">
            <span className="input-group-text" id="basic-addon3">https://example.com/users/</span>
        </div>
        <input type="text" className="form-control" id="basic-url" aria-describedby="basic-addon3"/>
        </div>

        <div className="input-group mb-3">
        <div className="input-group-prepend">
            <span className="input-group-text">$</span>
        </div>
        <input type="text" className="form-control" aria-label="Amount (to the nearest dollar)"/>
        <div className="input-group-append">
            <span className="input-group-text">.00</span>
        </div>
        </div>

        <div className="input-group">
        <textarea className="form-control" aria-label="With textarea"></textarea>
        </div>
        <button className="btn btn-primary my-3" onClick={handleUpClick}>Submit</button>
    </div>
  )
}
