import React, {useState} from 'react'


export default function TextForm(props) {
    const handleUpClick = () => {
        // console.log("The button is clicked" + text);
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleOnChange = (event) => {
        setText(event.target.value);
    }
    const [text, setText] = useState("Enter text here...");
    //setText("Enter the fucking text here.....");
  return (
    <>
    <div className="container">
        <h1>{props.heading}</h1>

        <div className="input-group">
            <textarea className="form-control" aria-label="With textarea" value={text} onChange={handleOnChange}></textarea>
        </div>
        <button className="btn btn-primary my-3" onClick={handleUpClick}>Submit</button>
    </div>
    <div className="container">
        <h1>For Results</h1>
        <p>There are {text.length} characters in the above field</p>
        <p>There are {text.split(" ").length} Words in the above field</p>
        <p>IT will Take {0.008 * text.split(" ").length} Minutes to read the above data in the input field...</p>
        <h3>Preview:</h3>
        <p>{text}</p>
    </div>
    </>
  )
}
