import React, {useState} from 'react'


export default function TextForm(props) {
    const handleUpClick = () => {
        // console.log("The button is clicked" + text);
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleOnChange = (event) => {
        setText(event.target.value);
    }
    const [text, setText] = useState("Enter text here...");
    //setText("Enter the fucking text here.....");
  return (
    <div className="container">
        <h1>{props.heading}</h1>

        <div className="input-group">
            <textarea className="form-control" aria-label="With textarea" value={text} onChange={handleOnChange}></textarea>
        </div>
        <button className="btn btn-primary my-3" onClick={handleUpClick}>Submit</button>
    </div>
  )
}
